mapper.select:
select *
from (
         select @rownum:=@rownum+1 rownum, b.* from board b, (select @rownum:=0) tmp
         order by bno desc
     ) list

where rownum > (#{pageNum}-1)*#{amount} and rownum <= #{pageNum} * #{amount}

select bno, title, content, regdate, cnt, id
from board
where bno=#{bno}

mapper.update:
update board
set cnt = cnt + 1
where bno = #{bno}

update board set
                 title=#{title}, content=#{content}
where bno=#{bno}
